import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import { fetchStockData, fetchStockStats } from "../services/apiService";
import "./StockDetail.css";

const StockDetail = () => {
  const { symbol } = useParams();
  const [stockData, setStockData] = useState(null);
  const [stockStats, setStockStats] = useState(null);
  const [timeRange, setTimeRange] = useState("1M"); // 1D, 1W, 1M, 3M, 1Y
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchStockData(symbol, timeRange);
        const stats = await fetchStockStats(symbol);

        setStockData(data);
        setStockStats(stats);
      } catch (error) {
        console.error(`Error fetching data for ${symbol}:`, error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [symbol, timeRange]);

  const handleTimeRangeChange = (newRange) => {
    setTimeRange(newRange);
    setLoading(true);
  };

  if (loading) {
    return <div className="loading">Loading stock data...</div>;
  }

  if (!stockData || !stockStats) {
    return <div className="error">Failed to load stock data.</div>;
  }

  const lastPrice = stockData.priceData[stockData.priceData.length - 1].close;
  const previousClose =
    stockData.priceData[stockData.priceData.length - 2].close;
  const priceChange = lastPrice - previousClose;
  const percentChange = (priceChange / previousClose) * 100;

  return (
    <div className="stock-detail-container">
      <div className="stock-header">
        <h1>
          {stockData.companyName} ({symbol})
        </h1>
        <div className="price-container">
          <div className="current-price">${lastPrice.toFixed(2)}</div>
          <div
            className={`price-change ${
              priceChange >= 0 ? "positive" : "negative"
            }`}
          >
            {priceChange >= 0 ? "+" : ""}
            {priceChange.toFixed(2)} ({percentChange.toFixed(2)}%)
          </div>
        </div>
      </div>

      <div className="time-range-selector">
        {["1D", "1W", "1M", "3M", "1Y"].map((range) => (
          <button
            key={range}
            className={timeRange === range ? "active" : ""}
            onClick={() => handleTimeRangeChange(range)}
          >
            {range}
          </button>
        ))}
      </div>

      <div className="chart-container">
        <h2>Price History</h2>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={stockData.priceData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis domain={["auto", "auto"]} />
            <Tooltip />
            <Legend />
            <Line
              type="monotone"
              dataKey="close"
              stroke="#8884d8"
              name="Close Price"
            />
          </LineChart>
        </ResponsiveContainer>
      </div>

      <div className="volume-chart-container">
        <h2>Trading Volume</h2>
        <ResponsiveContainer width="100%" height={200}>
          <BarChart data={stockData.priceData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="volume" fill="#82ca9d" name="Volume" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      <div className="stock-info-container">
        <div className="stock-stats">
          <h2>Key Statistics</h2>
          <table>
            <tbody>
              <tr>
                <td>Open</td>
                <td>
                  $
                  {stockData.priceData[
                    stockData.priceData.length - 1
                  ].open.toFixed(2)}
                </td>
              </tr>
              <tr>
                <td>High</td>
                <td>
                  $
                  {stockData.priceData[
                    stockData.priceData.length - 1
                  ].high.toFixed(2)}
                </td>
              </tr>
              <tr>
                <td>Low</td>
                <td>
                  $
                  {stockData.priceData[
                    stockData.priceData.length - 1
                  ].low.toFixed(2)}
                </td>
              </tr>
              <tr>
                <td>Volume</td>
                <td>
                  {stockData.priceData[
                    stockData.priceData.length - 1
                  ].volume.toLocaleString()}
                </td>
              </tr>
              <tr>
                <td>Market Cap</td>
                <td>${stockStats.marketCap.toLocaleString()}</td>
              </tr>
              <tr>
                <td>P/E Ratio</td>
                <td>{stockStats.peRatio.toFixed(2)}</td>
              </tr>
              <tr>
                <td>52-Week High</td>
                <td>${stockStats.fiftyTwoWeekHigh.toFixed(2)}</td>
              </tr>
              <tr>
                <td>52-Week Low</td>
                <td>${stockStats.fiftyTwoWeekLow.toFixed(2)}</td>
              </tr>
            </tbody>
          </table>
        </div>

        <div className="company-info">
          <h2>About {stockData.companyName}</h2>
          <p>{stockData.description}</p>
          <div className="company-details">
            <p>
              <strong>Sector:</strong> {stockData.sector}
            </p>
            <p>
              <strong>Industry:</strong> {stockData.industry}
            </p>
            <p>
              <strong>CEO:</strong> {stockData.ceo}
            </p>
            <p>
              <strong>Employees:</strong> {stockData.employees.toLocaleString()}
            </p>
            <p>
              <strong>Headquarters:</strong> {stockData.city}, {stockData.state}
            </p>
          </div>
        </div>
      </div>

      <div className="action-buttons">
        <Link to={`/predictions/${symbol}`} className="action-button">
          View Price Predictions
        </Link>
        <button className="action-button">Add to Watchlist</button>
      </div>
