const API_BASE_URL =
  process.env.REACT_APP_API_BASE_URL || "http://localhost:8081/api";

export const fetchStockData = async (symbol, timeRange) => {
  try {
    const response = await fetch(
      `${API_BASE_URL}/stocks/${symbol}/data?timeRange=${timeRange}`
    );

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    const data = await response.json();
    return data; // You can modify this to return only specific parts of the data if needed
  } catch (error) {
    console.error("Error fetching stock data:", error);
    throw error;
  }
};

export const fetchMarketSummary = async () => {
  try {
    const response = await fetch(`${API_BASE_URL}/analysis/trends`);

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    const data = await response.json();

    // Адаптирај ги податоците за dashboard
    return {
      trend: data.market_trend,
      confidence: data.confidence,
      volume: 153287000, // Пример податоци
      activeSymbols: data.symbol_trends.length,
      chartData: generateDummyChartData(7), // Функција што создава dummy податоци
    };
  } catch (error) {
    console.error("Error fetching market summary:", error);
    throw error;
  }
};

export const fetchTopGainers = async () => {
  // Во реалноста, ова би бил API повик до серверот
  // Враќаме пример податоци за UI демонстрација
  return [
    { symbol: "AAPL", price: 150.25, change: 3.75, percentChange: 2.56 },
    { symbol: "MSFT", price: 280.45, change: 5.23, percentChange: 1.9 },
    { symbol: "GOOGL", price: 2750.8, change: 45.3, percentChange: 1.67 },
    { symbol: "AMZN", price: 3320.15, change: 48.75, percentChange: 1.49 },
    { symbol: "FB", price: 325.85, change: 4.25, percentChange: 1.32 },
  ];
};

export const fetchTopLosers = async () => {
  // Пример податоци
  return [
    { symbol: "IBM", price: 142.65, change: -2.35, percentChange: -1.62 },
    { symbol: "INTC", price: 53.8, change: -0.75, percentChange: -1.37 },
